/******************************** Absolute Process******************/
long absolute(long value) 
{
	if (value < 0) {
	return -value;
	}
	else {
	return value;  
	}
}
			
/******************************** Moving Avarage Process******************/		
void movingAvarage(void)
{
	for ( int idx=0;idx<256; idx++)
	{
		if ( idx < 0  ||  idx>255)
		{
			movAvData[idx] = Reflectance[idx];
		}
		else 
		{
			movAvData[idx] =
			( 
			Reflectance[idx-1]+
			Reflectance[idx]+
			Reflectance[idx+1]) / 3;
		}
	}
}	
	
	
/******************************** Max Value Process******************/
uint16_t find_maximum(uint16_t a[], uint16_t n) 
{
	uint16_t  	c, index;
	max = a[0];
	index = 0;
	for (c = 1; c < n; c++) 
	{
		if (a[c] > max) 
		{
			index = c;
			max = a[c];
		}
	}
	return max;
}

